// Exit if accessed directly
if ( ! defined( 'ABSPATH' ) ) exit;

// Load text domain for translations
load_plugin_textdomain( 'super-simple-social-sharing', false, dirname( plugin_basename( __FILE__ ) ) . '/languages/' );


add_shortcode( 'super-simple-social-sharing', 'ssss_shortcode' );
/**
 * Generate the shortcode output for SSSS
 * @return string
 */
function ssss_shortcode( $atts ) {

  // Shortcode atts
	$atts = shortcode_atts( array(
		'header'         => apply_filters( 'ssss_header', __( 'Share this article:', 'super-simple-social-sharing' ) ),
		'social'         => array( 'facebook', 'twitter', 'google', 'email' ),
		'twitter_handle' => '',
    'email_subject'  => apply_filters( 'ssss_email_subject', __( 'An article worth sharing:', 'super-simple-social-sharing' ) ),
    'email_body'     => apply_filters( 'ssss_email_body', __( 'I read this article and wanted to share it with you. Here is the link:', 'super-simple-social-sharing' ) ),
  ), $atts );

  // Correct for custom Yoast Twitter title
  $yoast = get_post_meta( get_the_ID(), '_yoast_wpseo_twitter-title', true );
  $twitter_title = $yoast ? $yoast : ssss_title();

  // Get Titter handle if one is provided
  $twitter_handle = $atts['twitter_handle'] ? sprintf( '&via=%s', $atts['twitter_handle'] ) : '';

  //Page Attributes
	$page_attributes = array (
		'title'       	   => ssss_title(),
		'permalink'        => get_the_permalink(),
		'twitter_handle'   => $twitter_handle,
    'twitter_title'    => $twitter_title,
		'home'             => home_url(),
		'image'            => ssss_featured_image(),
		//'description'   => $description ? sprintf( '&summary=%s', $description ) : '', // Needed for Linkedin
		'description'		   => '',
    'email_subject'	   => $atts['email_subject'],
		'email_body'		   => $atts['email_body'],
	);

  // Get the icons and associated links
	$icons = ssss_icons();
	$links = ssss_links( $page_attributes );

  $header = $atts['header'] ? '<h3>' . $atts['header'] . '</h3>' : '';

	$output = '<div class="super-simple-social-sharing">';
  $output .= $header;

	foreach( $atts['social'] as $social ) {
		$link_start = '<a href="' . $links[$social] . '" class="'. $social . '" target="_blank">';
		$icon       = $icons[$social];
		$link_end   = '</a>';

		$output .= $link_start . $icon . $link_end;
	}

	$output .= '</div>';

	return $output;
}

/**
 * Set the post title for sharing
 *
 * @return string uses Yoast title if it exists, post title otherwise
 */
function ssss_title() {
	return apply_filters( 'ssss_posttitle', the_title_attribute( 'echo=0' ) );
}

/**
 * Retrieve the featured image
 *
 * @return string if there is a featured image, return the URL
 */
function ssss_featured_image() {
	$featured_image = has_post_thumbnail() ? get_post_thumbnail_id() : ssss_get_fallback_image();
	$image          = wp_get_attachment_image_src( $featured_image, 'large', false );
	$url            = isset( $image[0] ) ? $image[0] : '';
	return apply_filters( 'ssss_image_url', $url );
}

/**
 * If there is no featured image, use the first image attached to the post/page as the fallback.
 *
 * @return bool
 */
function ssss_get_fallback_image() {
	$image_ids = array_keys(
		get_children(
			array(
				'post_parent'    => get_the_ID(),
				'post_type'      => 'attachment',
				'post_mime_type' => 'image',
				'orderby'        => 'menu_order',
				'order'          => 'ASC',
				'numberposts'    => 1,
			)
		)
	);

	if ( isset( $image_ids[0] ) ) {
		return $image_ids[0];
	}

	return false;
}

/**
 * Get all of the social links
 *
 * @return array
 */
function ssss_links( $page_attributes ) {

	$links = array(
		'facebook' 	=> 'http://www.facebook.com/sharer/sharer.php?u=' . $page_attributes['permalink'],
		'twitter'  	=> 'https://twitter.com/intent/tweet?text=' . $page_attributes['twitter_title'] . '&url=' . $page_attributes['permalink'] . $page_attributes['twitter_handle'],
		'google'	 	=> 'https://plus.google.com/share?url=' . $page_attributes['permalink'],
		'pinterest'	=> '',
		'linkedin'	=> '',
		'reddit'		=> 'https://www.reddit.com/submit?url=' . $page_attributes['permalink'],
		'email'			=> 'mailto:?body='. $page_attributes['email_body'] . ' ' . $page_attributes['permalink'] . '&subject=' . $page_attributes['email_subject'] . ' ' . $page_attributes['title'],
	);

	return apply_filters( 'ssss-links', $links );
}

