add_action( 'wp_enqueue_scripts', 'ssss_frontend_scripts_enqueue' );
/**
 * Loads scripts to the frontend
 */
function ssss_frontend_scripts_enqueue() {
    wp_enqueue_style( 'ssss-frontend-styles',  plugin_dir_url( __FILE__ ) . 'assets/css/ssss-styles.css' );
}


